                // check if alt is a valid shortname or unicode emoji
                if (_shortname_to_codepoint.ContainsKey(alt)) {
                    return alt;
                } else {
                    var codepoint = ToCodePoint(alt);
                    if (_codepoint_to_shortname.ContainsKey(codepoint)) {
                        return _codepoint_to_shortname[codepoint];
                    }
                }
            }

            return match.Value;
        }

        private static string ShortnameToImageCallback(Match match) {
            // check if the emoji exists in our dictionary
            var shortname = match.Value;
            if (_shortname_to_codepoint.ContainsKey(shortname)) {
                var codepoint = _shortname_to_codepoint[shortname];
                string alt = _unicode_alt ? ToUnicode(codepoint) : shortname;
                if (_use_icons) {
                    return string.Format(@"<i class=""eo eo-{0}"">{1}</i>", shortname.Replace("_", "-").Replace(":", ""), alt);
                } else {
                    return string.Format(@"<img class=""eo"" src=""{0}{1}.svg"" alt=""{2}"" />", _image_path, codepoint.ToUpper(), alt);
                }
            }

            // we didn't find a replacement so just return the entire match
            return match.Value;
        }

        /// <summary>
