        public static string ShortnameToUnicode(string str) {
            if (str != null) {
                str = Regex.Replace(str, IGNORE_PATTERN + "|" + SHORTNAME_PATTERN, ShortnameToUnicodeCallback, RegexOptions.IgnoreCase);
